#include "metaLanguageObject.h"

/* PNG */
static const wxString s_translation_64_png = wxT("");

wxIcon CMetaObjectLanguage::GetIcon() const
{
	return GetIconGroup();
}

wxIcon CMetaObjectLanguage::GetIconGroup()
{
	static wxIcon icon =
		CBackendPicture::GetIconFromBase64(s_translation_64_png, wxSize(16, 16));

	return icon;
}